 import React, {useRef} from 'react';
 import logo from './logo.svg';
 import mock1 from './assets/mock.svg';
 import  { Toolbar, AppBar } from '@material-ui/core';
 import {Row} from 'react-grid-system';
 import './App.css';
import Grid from './components/grid';
import PlatformButtons from './components/buttons';
import { githubLink } from './models/constants';

const _scrollController = (ref: any) => ref.current.scrollIntoView({behavior: "smooth", block: "start"});


function Body() {
  return (
    <React.Fragment>
      <div className="Body">
      <div className={["Header", "Title"].join(" ")}>Taiyaki</div>
      <div className={["Desc", "Roboto"].join(" ")}>Watch and sync your anime</div>
      <div className="Container-items">
      <PlatformButtons 
      platform={"ios"}
      onClick={() => window.open(githubLink + "/releases")}
      />
      <PlatformButtons 
      platform={"android"}
      onClick={() => window.open(githubLink + "/releases")}
      />
      </div>
      <PlatformButtons 
      platform={"github"}
      onClick={() => window.open(githubLink)}
      />
      <span
        style={{fontStyle: "italic"}}
      >Note: Taiyaki is currently in Beta. You may encounter bugs</span>
      </div>
    </React.Fragment>
  );
}

function LogoView() {
  return (
    <React.Fragment>
      <div className="Logo-view">
        <img  src={logo} className="Logo" alt="logo"/>
        <Body/>
      </div>
    </React.Fragment>
  );
}

function Image() {
  return (
    <img src={mock1} className="Mock1" alt="mock" style={{alignSelf: "center"}}/>
  );
}




function App() {
  const _ref = useRef(null);
  const _scroll = () => _scrollController(_ref);
  return (
   <React.Fragment>
   <div className="App">
   <header className="App-header">

   <AppBar position="fixed">
     <Toolbar className="Tool-bar">{
       <>
       <a href="/" style={{textDecoration: "none", color: "white"}}>
       <a className={["Header", "Roboto"].join(" ")}
       >Taiyaki</a>
       <span> - Watch and sync</span>
       </a>
       <a
       ref={_scrollController}
       className="App-link"
       onClick={_scroll}
       >About
       </a>
       </>
     }</Toolbar>
   </AppBar>
   </header>

   <Row align="center" className="Row-column" justify="center">
   <LogoView/>
   <Image/>
   </Row>

     <Row align="center" justify="around" className="Row-second">
     <Grid key={"1"} data={_data1} title="About"/>
     </Row>
   </div>
   </React.Fragment>
  )
}

export default App;

