 import React from 'react';
 import logo from './logo.svg';
 import mock2 from './assets/mock2.jpg'
 import  { Toolbar, AppBar } from '@material-ui/core';
 import {Container, Row, Col} from 'react-grid-system';
 import './App.css';
import Grid from './components/grid';
import PlatformButtons from './components/buttons';


function Body() {
  return (
    <React.Fragment>
      <div className="Body">
      <div className={["Header", "Title"].join(" ")}>Taiyaki</div>
      <div className={["Desc", "Roboto"].join(" ")}>Watch and sync your anime</div>
      <Row>
      <PlatformButtons platform={"ios"}/>
      <PlatformButtons platform={"android"}/>
      </Row>
      <PlatformButtons platform={"github"}/>
      <span>Note: Taiyaki is currently in Beta. You may encounter bugs</span>
      </div>
    </React.Fragment>
  );
}

function LogoView() {
  return (
    <React.Fragment>
      <div className="Logo-view">
        <img src={logo} className="Logo" alt="logo"/>
        <Body/>
      </div>
    </React.Fragment>
  );
}

function Image() {
  return (
    <div>
    <img src={mock2} className="Mock1" alt="mock"/>
  </div>
  );
}




function App() {
  return (
   <React.Fragment>
   <div className="App">
   <header className="App-header">
   <AppBar position="fixed">
     <Toolbar className="Tool-bar">{
       <>
       <a href="/" style={{textDecoration: "none", color: "white"}}>
       <a className={["Header", "Roboto"].join(" ")}
       >Taiyaki</a>
       <span> - Watch and sync</span>
       </a>
       <a href="download"
       className="App-link"
       style={{textDecoration: 'none'}}
       >Download
       </a>
       </>
     }</Toolbar>
   </AppBar>
   </header>

   <Row justify="around" align="stretch" className="Row-column" style={{width: '100%'}} >
   <LogoView/>
   <Image/>
   <div></div>
   </Row>
     <Row align="center" justify="around">
     <Grid key={"1"} data={_data1}/>
     <Grid key={"2"}/> 
     </Row>
   </div>
   </React.Fragment>
  )
}

export default App;

