import React, {FC} from 'react';
import { PlatformTypes, platformToText, platformToIcon } from '../models/platform_enums';
import '../styles/buttons.css';
import {githubLink} from '../models/constants';


interface Props {
    platform: PlatformTypes;

}

const PlatformButtons: FC<Props> = (props) => {
    return (
        <a
                href={(props.platform === "github") ? githubLink: (githubLink+"/releases") }
                style={{textDecoration: 'none', color: "white"}}
            >
        <div className={["Button-outline", props.platform].join(" ")} key={props.platform}>
            <a>{props.platform === "github" ? "View" : "Download"} on {platformToText.get(props.platform)}</a>

            <img src={platformToIcon.get(props.platform)} className="logo"/>
            </div> 
            </a>
    );
}

export default PlatformButtons;
