 import React from 'react';
 import logo from './logo.svg';
 import mock2 from './assets/mock3.png'
 import mock3 from './assets/mock2.png'
 import  { Toolbar, AppBar } from '@material-ui/core';
 import {Row} from 'react-grid-system';
 import './App.css';
import Grid from './components/grid';
import PlatformButtons from './components/buttons';
import { githubLink } from './models/constants';


function Body() {
  return (
    <React.Fragment>
      <div className="Body">
      <div className={["Header", "Title"].join(" ")}>Taiyaki</div>
      <div className={["Desc", "Roboto"].join(" ")}>Watch and sync your anime</div>
      <div className="Container-items">
      <PlatformButtons 
      platform={"ios"}
      onClick={() => window.open(githubLink + "/releases")}
      />
      <PlatformButtons 
      platform={"android"}
      onClick={() => window.open(githubLink + "/releases")}
      />
      </div>
      <PlatformButtons 
      platform={"github"}
      onClick={() => window.open(githubLink)}
      />
      <span
        style={{fontStyle: "italic"}}
      >Note: Taiyaki is currently in Beta. You may encounter bugs</span>
      </div>
    </React.Fragment>
  );
}

function LogoView() {
  return (
    <React.Fragment>
      <div className="Logo-view">
        <img  src={logo} className="Logo" alt="logo"/>
        <Body/>
      </div>
    </React.Fragment>
  );
}

function Image() {
  return (
    <div className="Mock-view">
    <img src={mock2} className="Mock1" alt="mock"/>
    <img src={mock3} className="Mock2" alt="mock"/>
  </div>
  );
}




function App() {
  return (
   <React.Fragment>
   <div className="App">
   <header className="App-header">

   <AppBar position="fixed">
     <Toolbar className="Tool-bar">{
       <>
       <a href="/" style={{textDecoration: "none", color: "white"}}>
       <a className={["Header", "Roboto"].join(" ")}
       >Taiyaki</a>
       <span> - Watch and sync</span>
       </a>
       <a href="download"
       className="App-link"
       style={{textDecoration: 'none'}}
       >Download
       </a>
       </>
     }</Toolbar>
   </AppBar>
   </header>

   <div className="Row-column">
   <LogoView/>
   <Image/>
   </div>

     <Row align="center" justify="around">
     <Grid key={"1"} data={_data1}/>
     <Grid key={"2"}/> 
     </Row>
   </div>
   </React.Fragment>
  )
}

export default App;

